import React, { useRef, useEffect } from 'react'
import { Power3, gsap, Power4 } from 'gsap'
import { ScrollTrigger } from 'gsap/dist/ScrollTrigger'

import styles from './HeroSection.module.scss'

gsap.registerPlugin(ScrollTrigger)

const Hero = () => {
  let app = useRef(null)
  let images = useRef(null)
  let content = useRef(null)

  // let tl = gsap.timeline({
  //   scrollTrigger: {
  //     trigger: content,
  //     toggleActions: 'play reverse',
  //     start: 'top 240px',
  //     end: 'top 400px',
  //     //pin: true,
  //     markers: true,
  //     scrub: 1,
  //   },
  // })
  // let tl = gsap.timeline({
  //   scrollTrigger: {
  //     trigger: app,
  //     scrub: true,
  //     pin: true,
  //     start: 'top top',
  //     end: '+=100%',
  //     markers: true,
  //   },
  // })

  useEffect(() => {
    //Images Vars
    const firstImg = images.firstElementChild
    const secondImg = images.lastElementChild

    //Content Vars
    //const headlineFirst = content.children[0].children[0].children
    const headlineSecondChild1 =
      content.children[0].children[0].children[0].children[0]
    const headlineSecondChild2 =
      content.children[0].children[0].children[0].children[1]
    const headlineSecondChild3 =
      content.children[0].children[0].children[0].children[2]
    const headlineSecondChild4 =
      content.children[0].children[0].children[0].children[3]
    const headlineSecondChild5 =
      content.children[0].children[0].children[0].children[4]
    const headlineSecondChild6 =
      content.children[0].children[0].children[0].children[5]
    const headlineSecondChild7 =
      content.children[0].children[0].children[0].children[6]
    const headlineSecondChild8 =
      content.children[0].children[0].children[0].children[7]

    const headlineThirdChild1 =
      content.children[0].children[1].children[0].children[0]

    console.log(headlineThirdChild1)

    const contentP = content.children[1]
    const contentButton = content.children[2]

    gsap.to(app, {
      css: { visibility: 'visible' },
      ease: Power3.easeOut,
    })

    //Images Animations
    gsap.from(firstImg.children, {
      y: -500,
      opacity: 0,
      scale: 1.5,
      ease: Power4.easeOut,
      delay: 0.7,
      duration: 0.8,
    })
    gsap.from(secondImg.children, {
      y: 500,
      opacity: 0,
      scale: 1.5,
      ease: Power4.easeOut,
      delay: 0.7,
      duration: 0.8,
    })

    // Content Animations

    // gsap.from(
    //   headlineFirst,
    //   {
    //     y: 170,
    //     ease: 'Power4.easeOut',
    //     skewY: 20,
    //     duration: 0.3,
    //     delay: 0.5,
    //   },
    // )
    gsap.from(
      [
        headlineSecondChild1,
        headlineSecondChild2,
        headlineSecondChild3,
        headlineSecondChild4,
        headlineSecondChild5,
        headlineSecondChild6,
        headlineSecondChild7,
        headlineSecondChild8,
      ],
      {
        stagger: { each: 0.05 },
        y: 600,
        ease: 'Power4.easeOut',
        skewY: 50,
        delay: 0.3,
        duration: 0.8,
      }
    )
    // tl.from(
    //   contentP,
    //   1,
    //   {
    //     y: 20,
    //     opacity: 0,
    //     ease: 'back',
    //   },
    //   '<'
    // )
    //   tl.from(
    //     contentButton,
    //     {
    //       y: 20,
    //       opacity: 0,
    //       ease: 'back',
    //     },
    //     '<'
    //   )
  })

  //TO SOLVE MY PARALLAX EFFECT, I HAVE TO DET TIMELINE

  useEffect(() => {
    //const headlineFirst = content.children[0].children[0].children
    const headlineSecond = content.children[0].children[0].children
    const headlineThird = content.children[0].children[1].children
    // const contentP = content.children[1]
    // const contentButton = content.children[2]

    gsap
      .timeline({
        scrollTrigger: {
          trigger: content,
          toggleActions: 'restart pause reverse',
          start: 'top 33.4%', // When the top of content elt is at 33.4 % of the viewport, runs the animation
          end: 'bottom 300px',
          pin: true,
          markers: true,
          scrub: 1,
          pinSpacing: true,
        },
      })
      .fromTo(
        headlineThird,
        {
          //y: 500,
          ease: 'Power4.easeOut',
          //skewY: 30,
          delay: 0.3,
          duration: 0.8,
        },
        {
          y: 500,
          //ease: 'Power4.easeOut',
          //skewY: 0,
          // delay: 0.3,
          // duration: 0.8,
        }
      )
  })

  return (
    <div className={styles.hero} ref={(el) => (app = el)}>
      <div className={styles.container}>
        <div className={styles.heroInner}>
          <div className={styles.heroContent}>
            <div
              className={styles.heroContentInner}
              ref={(el) => (content = el)}
            >
              <div className={styles.heroContentLine}>
                {/* <div style={{ overflow: 'hidden' }}>
                  <p className={styles.small}>Messie NUNGI-PAMBU</p>
                </div> */}
                <div className={styles.heroContentLineInner}>
                  <div style={{ display: 'flex' }}>
                    <h1>F</h1>
                    <h1>R</h1>
                    <h1>O</h1>
                    <h1>N</h1>
                    <h1>T</h1>
                    <h1>E</h1>
                    <h1>N</h1>
                    <h1>D</h1>
                  </div>
                </div>
                <div className={styles.heroContentLineInner}>
                  <div style={{ display: 'flex' }}>
                    <h1>D</h1>
                    <h1>E</h1>
                    <h1>V</h1>
                    <h1>E</h1>
                    <h1>L</h1>
                    <h1>O</h1>
                    <h1>P</h1>
                    <h1>E</h1>
                    <h1>R</h1>
                  </div>
                </div>
              </div>
              <div className={styles.heroTabletImage}></div>
            </div>
          </div>
          <div className={styles.heroImages}>
            <div className={styles.heroImagesInner} ref={(el) => (images = el)}>
              <div className={styles.heroImage} id={styles.first}>
                <img src='/images/image2.jpg' alt='front-img' />
              </div>
              <div className={styles.heroImage} id={styles.second}>
                <img src='/images/image1.jpg' alt='back-img' />
              </div>
            </div>
          </div>
        </div>
        <div className={styles.heroTabletPara}>
          <p>
            Hi! I'm <strong>Messie</strong>, a{' '}
            <strong>Frontend Developer</strong> based in France.
            <br />
            Sensitive to design, i stress the smaller details to deliver
            pixel-perfect products.
            <br />
            <br />I know how precious time is, especially in business, and how
            challenging it can be to retain the user attention, so i adapt
            quickly and listen carefully to your needs and i constantly try to
            always offer that little more than what make the difference.
          </p>
          <p id={styles.tag}>
            <span>#ReactJS</span>
            <span className={styles.even}>#GatsbyJS</span>
            <span className={styles.odd}>#NextJS</span>
            <span className={styles.brown}>#Styled-Components</span>
            <br />
            <span className={styles.odd}>#JavaScript</span>
            <span>#Redux</span>
            <span className={styles.even}>#Scss</span>
          </p>
        </div>
      </div>
    </div>
  )
}

export default Hero
