import React, { useEffect, useRef } from "react";
import { gsap } from "gsap/dist/gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";

import styles from "./ProjectItemLeft.module.scss";

gsap.registerPlugin(ScrollTrigger);

const ProjectItemLeft = ({ title, subtitle, technos, image }) => {
  let h3 = useRef(null);
  let para = useRef(null);
  let photo = useRef(null);

  useEffect(() => {
    gsap.from(h3, 1, {
      scrollTrigger: {
        trigger: h3,
        toggleActions: "restart play reverse",
        end: "top 94%",
      },
      y: 120,
      ease: "back",
    });
    gsap.from(para, 1, {
      scrollTrigger: {
        trigger: para,
        toggleActions: "restart play reverse",
        end: "top 94%",
      },
      opacity: 0,
      letterSpacing: "1rem",
      //delay: 5,
    });
    gsap.from(photo, 1.2, {
      scrollTrigger: {
        trigger: photo,
        toggleActions: "restart play reverse",
        end: "top 100%",
      },
      y: 250,
      scale: 1.2,
      ease: "back",
    });
  });

  return (
    <div className={styles.container}>
      <div className={styles.content}>
        <p>
          <span ref={(el) => (h3 = el)}>{title}</span>
        </p>
        <p>
          <span ref={(el) => (para = el)}>{subtitle}</span>
        </p>
        <div className={styles.technos}>
          <h5>Main Technos :</h5>
          <p>{technos}</p>
        </div>
      </div>
      <div className={styles.image}>
        <img src={image} alt="Project" ref={(el) => (photo = el)} />
      </div>
    </div>
  );
};

export default ProjectItemLeft;
