import React, { useEffect, useRef } from "react";
import { gsap } from "gsap/dist/gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";

import styles from "./ProjectItemLeft.module.scss";

gsap.registerPlugin(ScrollTrigger);

const ProjectItemLeft = ({ title, subtitle, technos, image }) => {
  let h3 = useRef(null);
  let para = useRef(null);
  let photo = useRef(null);
  let technosTitle = useRef(null);
  let technosPara = useRef(null);

  useEffect(() => {
    gsap.from(h3, 1, {
      scrollTrigger: {
        trigger: h3,
        toggleActions: "restart play reverse",
        end: "top 94%",
      },
      y: 200,
      ease: "back",
    });
    gsap.from(para, 3, {
      scrollTrigger: {
        trigger: para,
        toggleActions: "restart play reverse",
        end: "top 80%",
      },
      //opacity: 0,
      ease: "back",
      y: 200,
      //delay: 5,
    });
    gsap.from([technosPara, technosTitle], 1, {
      scrollTrigger: {
        trigger: [technosPara, technosTitle],
        toggleActions: "restart play reverse",
        end: "top 98%",
      },
      y: 200,
      ease: "back",
    });
    gsap.from(photo, 1.2, {
      scrollTrigger: {
        trigger: photo,
        toggleActions: "restart play reverse",
        end: "top 100%",
      },
      y: 250,
      scale: 1.2,
      ease: "back",
    });
  });

  return (
    <div className={styles.container}>
      <div className={styles.content}>
        <div className={styles.h3}>
          <h3 ref={(el) => (h3 = el)}>{title}</h3>
        </div>
        <div style={{ overflow: "hidden" }}>
          <p>
            <span ref={(el) => (para = el)}>{subtitle}</span>
          </p>
        </div>

        <div className={styles.technos}>
          <h5 ref={(el) => (technosTitle = el)}>Main Technos :</h5>
          <p ref={(el) => (technosPara = el)}>{technos}</p>
        </div>
      </div>
      <div className={styles.image}>
        <img src={image} alt="Project" ref={(el) => (photo = el)} />
      </div>
    </div>
  );
};

export default ProjectItemLeft;
