import React, { useRef, useEffect } from 'react'
import { Power3, gsap, Power4 } from 'gsap'
import { ScrollTrigger } from 'gsap/dist/ScrollTrigger'

import styles from './HeroSection.module.scss'

gsap.registerPlugin(ScrollTrigger)

const Hero = () => {
  let app = useRef(null)
  let images = useRef(null)
  let content = useRef(null)

  let tl = gsap.timeline({ delay: 0.01 })

  useEffect(() => {
    //Images Vars
    const firstImg = images.firstElementChild
    const secondImg = images.lastElementChild

    //Content Vars
    const headlineFirst = content.children[0].children[0]
    const headlineSecond = content.children[0].children[1]
    const headlineThrid = content.children[0].children[2]
    const headlineFourth = content.children[0].children[3]
    const contentP = content.children[1]
    const contentButton = content.children[2]

    gsap.to(app, 0, {
      css: { visibility: 'visible' },
      ease: Power3.easeOut,
    })

    //Images Animations
    // gsap.from(firstImg, {
    //   //y: 1000,
    //   ease: Power3.easeOut,
    //   background: '#e85a4f',
    //   duration: 1,
    //   height: 0,
    // })
    gsap.from(firstImg.children, {
      y: -1200,
      opacity: 0,
      scale: 1.5,
      ease: Power4.easeOut,
      delay: 0.5,
      duration: 0.5,
    })
    // gsap.from(secondImg, {
    //   //y: 1280,
    //   ease: Power3.easeOut,
    //   background: '#e85a4f',
    //   duration: 1,
    //   height: 0,
    // })
    gsap.from(secondImg.children, {
      y: 1200,
      opacity: 0,
      scale: 1.5,
      ease: Power4.easeOut,
      delay: 0.5,
      duration: 0.5,
    })
    gsap.from(content, {
      scrollTrigger: {
        trigger: content,
        toggleActions: 'restart play reverse',
        start: 'top 290px',
        end: 'bottom 0px',
        pin: true,
        markers: true,
      },
      //y: -50,
      ease: Power3.easeOut,
    })

    //Content Animations
    tl.staggerFrom(
      [headlineFirst, headlineSecond, headlineThrid, headlineFourth],
      0.7,

      {
        y: 800,
        ease: 'Power3.easeOut',
        skewY: 40,
      },
      0.12,
      'Start'
    )
      .from(
        contentP,
        1,
        {
          y: 20,
          opacity: 0,
          ease: 'back',
        },
        1.2
      )
      .from(
        contentButton,
        1,
        {
          y: 20,
          opacity: 0,
          ease: 'back',
        },
        1.6
      )
  })

  // useEffect(() => {
  //   const headlineFirst = content.children[0].children[0]

  //   ScrollTrigger.create({
  //     trigger: headlineFirst,
  //     start: 'top 300px',
  //     end: '+=330px',
  //     markers: true,
  //     pin: true,
  //     css: {
  //       color: 'red',
  //     },
  //   })
  // })

  return (
    <div className={styles.hero} ref={(el) => (app = el)}>
      <div className={styles.container}>
        <div className={styles.heroInner}>
          <div className={styles.heroContent}>
            <div className={styles.heroContentInner}>
              <h1 ref={(el) => (content = el)}>
                <div className={styles.heroContentLine}>
                  <div className={styles.small}>Messie NUNGI-PAMBU</div>
                  <div className={styles.heroContentLineInner}>FRONTEND</div>
                  <div className={styles.heroContentLineInner}>DEVELOPER</div>
                </div>
              </h1>
              <div className={styles.heroTabletImage}></div>
            </div>
          </div>
          <div className={styles.heroImages}>
            <div className={styles.heroImagesInner} ref={(el) => (images = el)}>
              <div className={styles.heroImage} id={styles.first}>
                <img src='/images/image2.jpg' alt='front-img' />
              </div>
              <div className={styles.heroImage} id={styles.second}>
                <img src='/images/image1.jpg' alt='back-img' />
              </div>
            </div>
          </div>
        </div>
        <div className={styles.heroTabletPara}>
          <p>
            Hi! I'm <strong>Messie</strong>, a{' '}
            <strong>Frontend Developer</strong> based in France.
            <br />
            Sensitive to design, i stress the smaller details to deliver
            pixel-perfect products.
            <br />
            <br />I know how precious time is, especially in business, and how
            challenging it can be to retain the user attention, so i adapt
            quickly and listen carefully to your needs and i constantly try to
            always offer that little more than what make the difference.
          </p>
          <p id={styles.tag}>
            <span>#ReactJS</span>
            <span className={styles.even}>#GatsbyJS</span>
            <span className={styles.odd}>#NextJS</span>
            <span className={styles.brown}>#Styled-Components</span>
            <br />
            <span className={styles.odd}>#JavaScript</span>
            <span>#Redux</span>
            <span className={styles.even}>#Scss</span>
          </p>
        </div>
      </div>
    </div>
  )
}

export default Hero
