import React, { useEffect, useRef } from "react";
import { gsap, Power3 } from "gsap/dist/gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";

import styles from "./ProjectItem.module.scss";

gsap.registerPlugin(ScrollTrigger);

const ProjectItem = ({
  heroImage,
  title,
  role,
  challenges,
  technos,
  linkToSeeLive,
  imageOne,
  imageTwo,
  imageThree,
  tabletImageOne,
  tabletImageTwo,
  tabletImageThree,
  mobileImageOne,
  mobileImageTwo,
  mobileImageThree
}) => {

  let heroTitle = useRef(null);
  let image = useRef(null);
  let descriptionRoleTitle = useRef(null);
  let descriptionTechosTitle = useRef(null);
  let descriptionChallengeTitle = useRef(null);
  let descriptionRoleContent = useRef(null);
  let descriptionTechosContent = useRef(null);
  let descriptionChallengeContent = useRef(null);
  let descriptionLink = useRef(null);

  useEffect(() => {
    gsap.from(heroTitle, {
      y: 200,
      ease: "back",
      delay: 0.5,
      duration: 1.1
    });
    gsap.from(image, {
      width: "100vw",
      duration: 1.5
    });
    gsap.from([descriptionRoleTitle, descriptionTechosTitle, descriptionChallengeTitle], {
      scrollTrigger: {
        trigger: [descriptionRoleTitle, descriptionTechosTitle, descriptionChallengeTitle],
        toggleActions: "play",
      },
      y: 200,
      duration: 1
    });
    gsap.from([descriptionRoleContent, descriptionTechosContent, descriptionChallengeContent], {
      scrollTrigger: {
        trigger: [descriptionRoleContent, descriptionTechosContent, descriptionChallengeContent],
        toggleActions: "play",
      },
      y: 200,
      duration: 1,
      delay: .3
    });
    gsap.from(descriptionLink, {
      scrollTrigger: {
        trigger: descriptionLink,
        toggleActions: "play",
      },
      x: 200,
      duration: 1,
      delay: .3
    });
  })

  return (
    <>
    <div className={styles.container}>
      <div className={styles.hero}>
        <img 
        src={heroImage} 
        alt="Project Screen" 
        ref={(el) => (image = el)} />
        <div className={styles.heroTitle}>
          <h1 ref={(el) => (heroTitle = el)}>{title}</h1>
        </div>
      </div>
      <div className={styles.description}>
        <div className={styles.descriptionLeft}>
          <div>
            <div className={styles.titleContainer}>
              <h3 ref={(el) => (descriptionRoleTitle = el)}>Role :</h3>
            </div>
            <div className={styles.contentContainer}>
            <p ref={(el) => (descriptionRoleContent = el)}>{role}</p>
            </div>
          </div>
          <div>
            <div className={styles.titleContainer}>
              <h3 ref={(el) => (descriptionChallengeTitle = el)}>Challenges</h3>
            </div>
            <div className={styles.contentContainer}>
              <p ref={(el) => (descriptionChallengeContent = el)}>{challenges}</p>
            </div>
          </div>
        </div>
        <div className={styles.descriptionRight}>
          <div>
            <div className={styles.titleContainer}>
              <h3 ref={(el) => (descriptionTechosTitle = el)}>Technos :</h3>
            </div>
            <div className={styles.contentContainer}>
              <p ref={(el) => (descriptionTechosContent = el)}>{technos}</p>
            </div>
          </div>
          <div className={styles.descriptionRightLink}>
            <a 
            href={linkToSeeLive} 
            target="_blank" 
            rel="noopener noreferrer"
            ref={(el) => (descriptionLink= el)}
            >See live</a>
          </div>
        </div>
      </div>

      <p className={styles.screen}>DESKTOP</p>
      <div className={styles.images}>
          <img src={imageOne} alt="" />
          <img src={imageTwo} alt="" className={styles.imageTwo} />
          <img src={imageThree} alt="" className={styles.imageThree} />
      </div>

      <p className={styles.screen}>TABLET</p>
      <div className={styles.images}>
          <img src={tabletImageOne} alt="" id={styles.tabletImage} />
          <img src={tabletImageTwo} alt="" id={styles.tabletImage} className={styles.imageTwo} />
          <img src={tabletImageThree} alt="" id={styles.tabletImage} className={styles.imageThree} />
      </div>

      <p className={styles.screen}>MOBILE</p>
      <div className={styles.images}>
          <img src={mobileImageOne} alt="" id={styles.mobileImage} />
          <img src={mobileImageTwo} alt="" id={styles.mobileImage} className={styles.imageTwo} />
          <img src={mobileImageThree} alt="" id={styles.mobileImage} className={styles.imageThree} />
      </div>
      <div>Previous Project / next Project</div>
    </div>
    </>
  );
};

export default ProjectItem;
