import React, { useRef, useEffect } from 'react'
import { Power3, gsap, Power4 } from 'gsap'
import { ScrollTrigger } from 'gsap/dist/ScrollTrigger'

import styles from './HeroSection.module.scss'

gsap.registerPlugin(ScrollTrigger)

const Hero = () => {
  let app = useRef(null)
  let images = useRef(null)
  let content = useRef(null)
  let innerHero = useRef(null)
  let imagesText = useRef(null)

  useEffect(() => {
    //Images Vars
    const firstImg = images.firstElementChild
    const secondImg = images.lastElementChild

    //Content Vars
    //const headlineFirst = content.children[0].children[0].children
    const headlineSecondChild1 = content.children[0].children[0].children[0]
    const headlineSecondChild2 = content.children[0].children[0].children[1]
    const headlineSecondChild3 = content.children[0].children[0].children[2]
