    const headlineSecondChild5 = content.children[0].children[0].children[4]
    const headlineSecondChild6 = content.children[0].children[0].children[5]
    const headlineSecondChild7 = content.children[0].children[0].children[6]
    const headlineSecondChild8 = content.children[0].children[0].children[7]

    const headlineThirdChild1 = content.children[1].children[0].children[0]
    const headlineThirdChild2 = content.children[1].children[0].children[1]
    const headlineThirdChild3 = content.children[1].children[0].children[2]
    const headlineThirdChild4 = content.children[1].children[0].children[3]
    const headlineThirdChild5 = content.children[1].children[0].children[4]
    const headlineThirdChild6 = content.children[1].children[0].children[5]
    const headlineThirdChild7 = content.children[1].children[0].children[6]
    const headlineThirdChild8 = content.children[1].children[0].children[7]
    const headlineThirdChild9 = content.children[1].children[0].children[8]

    gsap.to(app, {
      css: { visibility: 'visible' },
      ease: Power3.easeOut,
    })

    //Images Animations
    gsap
      .timeline({
        scrollTrigger: {
          trigger: innerHero,
          toggleActions: 'restart pause reverse',
          start: 'top 70px', // When the top of content elt is at 33.4 % of the viewport, runs the animation
          end: 'bottom 0',
          pin: true,
          markers: true,
          scrub: 3,
          //pinSpacing: true,
        },
      })
      .from(firstImg.children, {
        y: -500,
        opacity: 0,
        scale: 1.5,
        ease: Power4.easeOut,
        delay: 0.7,
        duration: 0.8,
      })
      .from(secondImg.children, {
        y: 500,
        opacity: 0,
        scale: 1.5,
        ease: Power4.easeOut,
        delay: 0.7,
        duration: 0.8,
      })

      // Content Animations

